import socket
import time

byte_message = bytes.fromhex("")
opened_socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
opened_socket.settimeout(5)

while True:

	opened_socket.sendto(byte_message, ("toronto6.voip.ms", 5060))

	msgFromServer = opened_socket.recvfrom(4096)
	msg = "Message from Server {}".format(msgFromServer[0])
	print(msg)
	print("----")
